import useSWR from 'swr';

const fetcher = (url) => fetch(url).then((res) => res.json());

export function userData() {
    const { data, error } = useSWR('/api/user', fetcher);
    if (error) return {user: null}
    if (!data) return {user: null}
    return data;
}
