import useSWR from 'swr';

const fetcher = (url) => fetch(url).then((res) => res.json());

export function useUser() {
    const { data, setUserdata } = useSWR('/api/user', fetcher);
    const user = data && data.user;
    return [user, { setUserData }];
}
