import useSWR from 'swr';
import styled from 'styled-components';

let data = {};

export default function WelcomePage ({ data }) {
    return(
        <Container>
            <userName />
        </Container>
    );
}

const userName = function userInfo() {
    const { data, error } = useSWR('/api/users/name', fetch)

    if (error) return <h1>Uh, Oh!</h1>
    if (!data) return <h1>Hello, ...</h1>
    return <h1>Hello, {data.name}!</h1>
  }

const Container = styled.div`
    height: 100vh;
    min-width: 600px;
    width: 100%;
    background-color: #36454F;
    display: flex;
    align-items: center;
    h1 {
        margin: 0 auto;
        font-family: 'Montserrat', sans-serif;
        font-weight: 900;
        font-size: 85px;
        color: white;
    }
`;
