import styles from './backend-theme.module.css';

export default function BackendTheme (props) {
    return(
        <div className={styles.pageContainer}>
            <title>{props.pageName} | Spitzers.net</title>

            {/* Navigation / Logo bar w. profile pic and greeting */}
            <div className={styles.navbar}>
                <h3 className={styles.pageName}>{props.pageName}</h3>
                <div className={styles.siteTitleContainer}><h1 className={styles.siteTitle}>spitzers.net</h1></div>
                <div className={styles.menuGroup}>
                    <h5 id="profile-greeting" className={styles.shortGreeting}>Hi, <span id="userDisplayName">Aubin Spitzer</span></h5>
                    <img className={styles.profilePicture} id="profile-picture"></img>
                    <img className={styles.menuButton} id="menu-button"></img>
                </div>
            </div>

            {/* Main Page Body */}
            <div className={styles.mainContainer}>
                {/* Left Sidebar with Pages based on Role permission */}
                <div className={styles.pageSelector}>
                    <div className={styles.pageLink}><a>
                        <img src="/dashboard/icons/portal.svg"></img>
                        <h5 className={styles.pageSelectorText}>Portal</h5>
                    </a></div>
                </div>
                {/* Pass Components from page to this "theme" | Main Content Section */}
                <props.Content />
            </div>
        </div>
    );
}

