        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.1);
    }
    input:focus {
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.35);
        transition: 300ms;
    }
    input:hover {
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        transition: 300ms;
    }

    label {
        text-align: left;
        margin: 0 0 5px 0;
