import axios from 'axios';
import styled from 'styled-components';

let userData = {};

export default function WelcomePage ({ data }) {
    return(
        <Container onClick={showMessage}>
            <Nav><h3>Next.JS</h3><button href="/logout">Log Out</button></Nav>
            <h1 id="welcomeText"></h1>
            <p id="extraMessageContainer">This site is still under development and is more of an excersize to learn a MENN Stack (MongoDB, ExpressJS, NextJS, NodeJS! Thanks for checking it out!</p>
        </Container>
    );
}

async function updateUserName() {
    const response = await axios.get('/api/users');
    console.log(response);
    const titleElement = document.getElementById("welcomeText");
    titleElement.innerHTML = `Hello, ${response.data.user.name}!`;
    userData = response.data.user;
}
function showMessage () {
    document.getElementById("extraMessageContainer").classList.toggle("hidden");
}
updateUserName();
const Container = styled.div`
    height: 100%;
    width: 100%;
    background-color: #36454F;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: 900ms;
    h1 {
        margin: 0 0 0 0;
        font-family: 'Montserrat', sans-serif;
        font-weight: 900;
        font-size: 85px;
        color: white;
        padding: 50px;
        flex: 1;
    }
    p {
        margin: 0;
        font-family: 'Montserrat', sans-serif;
        font-weight: 400;
        font-size: 25px;
        color: white;
        padding: 50px;
        flex: 1;
    }
`;
const Nav = styled.div`
    height: 72px;
    width: 100%;
    background-color: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    h3 {
        font-family: 'Montserrat', sans-serif;
        font-weight: 700;
        font-size: 35px;
        color: white;
        margin-left: 25px;
        letter-spacing: 5px;
    }
`;
