//Component Imports
import Link from 'next/link';
import ButtonHelp from '../../components/ButtonHelp';
import ButtonHurt from '../../components/buttonHurt';
import Layout from '../../components/layout';

//Useful Stuff Imports
import { TwitchEmbed, TwitchChat, TwitchClip, TwitchPlayer } from 'react-twitch-embed';
import styled from 'styled-components';
import axios from 'axios';

function StreamerGameControl (props) {
    return (
        <Layout>
            <StreamerSidebar>

            </StreamerSidebar>
            <ContentWrapper>
                
                <RowOneWrapper>
                    <Twitch>
                        <TwitchEmbed 
                            channel="uthd"
                            id="uthd"
                            theme="dark"
                            muted
                            height="100%"
                            width="100%"
                        />
                    </Twitch>
                </RowOneWrapper>
                <RowTwoWrapper>
                    <GameControl>
                        <ButtonHelp title="Enchant Held Item"></ButtonHelp>
                        <ButtonHelp title="Refill Health"></ButtonHelp>
                        <ButtonHelp title="Give Items"></ButtonHelp>
                        <ButtonHelp title="Give Helpful Potion Effect"></ButtonHelp>
                        <ButtonHurt title="Give Annoying Potion Effect"></ButtonHurt>
                        <ButtonHurt title="Take items"></ButtonHurt>
                        <ButtonHurt title="Summon Mobs"></ButtonHurt>
                        <ButtonHurt title="Misc."></ButtonHurt>
                    </GameControl>
                </RowTwoWrapper>
                <RowThreeWrapper>
                    <Info>
                        <h3>Stats</h3>
                        <h5><span>Shards:</span>   100</h5>
                    </Info>
                </RowThreeWrapper>
            </ContentWrapper>
        </Layout>
    );
}

const ContentWrapper = styled.div`
    width: calc(100vw - 72px);
    margin-left: 72px;

    display: flex;
    flex-direction: column;
    align-items: center;
`;

const StreamerSidebar = styled.div`
    height: 100vh;
    width: 72px;
    z-index: 5;
    background-color: #1F2A39;
    position: fixed;
    display: flex;
`;
const RowOneWrapper = styled.div`
    min-height: 800px;
    width: 80vw;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    overflow: hidden;
    margin: 0;
`; 
const RowTwoWrapper = styled.div`
    justify-content: center;

    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -moz-flex;
    display: -webkit-flex;
    display: flex;

    margin: 0;
`;
const RowThreeWrapper = styled.div`
    width: 100%;
    justify-content: center;

    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -moz-flex;
    display: -webkit-flex;
    display: flex;
`;

const Twitch = styled.div`
    width: 80vw;
    height: 720px;
    background-color: gray;
    margin: 20px;
`;
const GameControl = styled.div`
    margin: 0 auto;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    justify-self: left;
`;
const Info = styled.div`
    min-height: 200px;
    min-width: 200px;

    box-shadow: 0px 0px 0px 3px #F0524C;
    border-radius: 8px;
    background: none;

    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 25px;
    h3 {
        text-align: center;
        font-size: 36px;
        margin: 0 auto;
    }
    h5 {
        text-align: center;
        font-size: 20px;
        margin: 0 auto;
        font-weight: 200;
    }
    span {
        font-size: 24px;
        font-weight: 600;
    }
`;


//Props getting
/*
export async function getStaticPaths() {
    const paths = await getAllPageSlugs();
    return {
        fallback: 'blocking',
        paths
    }
  }

export async function getStaticProps({ params }) {
    const pageData = await getPageDataFromSlug(params.slug);
    return {
        revalidate: 5,
        props: {pageData}
    }
}

async function getAllPageSlugs () {
    const pages = await getPages();
    return pages.map(page => {
        return {
            params: {
                slug: page.slug
            }
        }
    })
}*/

export default StreamerGameControl;
