//Component Imports
import Link from 'next/link';
import ButtonHelp from '../../components/buttonHelp';
import Layout from '../../components/layout';

//Useful Stuff Imports
import { TwitchEmbed, TwitchChat, TwitchClip, TwitchPlayer } from 'react-twitch-embed';
import styled from 'styled-components';
import axios from 'axios';

function StreamerGameControl (props) {
    return (
        <Layout>
            <ContentWrapper>
                <StreamerSidebar>

                </StreamerSidebar>
                <PrimaryWrapper>
                    <Twitch>
                        <TwitchEmbed 
                            channel="uthd"
                            id="uthd"
                            theme="dark"
                            muted
                            height="100%"
                            width="100%"
                        />
                    </Twitch>
                    <Info>
                        <p>Current Shards: 100</p>
                    </Info>
                    <GameControl>
                        <ButtonHelp title="Enchant Held Item"></ButtonHelp>
                        <ButtonHelp title="Refill Health"></ButtonHelp>
                        <ButtonHelp title="Enchant Held Item"></ButtonHelp>
                        <ButtonHelp title="Enchant Held Item"></ButtonHelp>
                    </GameControl>
                </PrimaryWrapper>
            </ContentWrapper>
        </Layout>
    );
}

const ContentWrapper = styled.div`
    width: 100%;
    height: auto;
    z-index: 1;

    display: flex;
    background-color: #101824;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    overflow: hidden;
`;

const StreamerSidebar = styled.div`
    height: 100%;
    min-width: 72px;
    z-index: 5;
    background-color: #1F2A39;
`;
const PrimaryWrapper = styled.div`
    height: 150vh;
    width: 100%;
    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -moz-flex;
    display: -webkit-flex;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    overflow: hidden;
`; 

const Twitch = styled.div`
    width: 75%;
    background-color: gray;
    margin: 20px;
`;
const GameControl = styled.div`
    display: flex;
`;
const Info = styled.div`
    margin: 0 auto;
`;


//Props getting
