import styled from 'styled-components';
import Layout from '../components/layout';

function Home () {
    return(
        <Layout>
            <Canvas>
                <Wrapper>
                    <Title>Play along with your favorite streamers</Title>
                    <ButtonWrapper>
                        <a href={`${process.env.hostUrl}/auth/twitch/?returnTo=stream`}></a><SignUpButton>Sign Up</SignUpButton>
                        <LearnMoreButton>Learn More</LearnMoreButton>
                    </ButtonWrapper>
                </Wrapper>
            </Canvas>

        </Layout>
    );
}

const Title = styled.h1`
    font-size: 96px;
    text-align: center;
    margin-bottom: 100px;
    color: #FF5953;
    text-shadow: 4px 6px 3px #101824;
`;

const Wrapper = styled.div`
    float: left;
    width: 75%;

    padding: 50px;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Canvas =styled.div`
    width: 100%;
    height: calc(100vh - 80px);
    background: url(/home/gaming-minecraft.png);
    background-size: cover;

    display: flex;
    align-items: center;
`;

const ButtonWrapper = styled.div`
    display: flex;

`;

const SignUpButton = styled.div`
    background-color: #9146FF;
    height: 44px;
    width: auto;
    padding: 0 50px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 5px;
    color: white;
    margin: 0 40px;
`;
const LearnMoreButton = styled.div`
    height: 44px;
    width: auto;
    padding: 0 50px;

    display: flex;
    align-items: center;
    justify-content: center;

    border: 2px dotted #FF5953;
    border-radius: 5px;
    color: white;
    margin: 0 40px;
`;

export default Home;
