import styled from 'styled-components'

import Card from '../stats-card'
import { useEffect, useState } from 'react';

/* Socket.io */
import { io } from "socket.io-client";

function Dashboard (props) {
    const [userData, setUserData] = useState();
    const [liveStats, setLiveStats] = useState();
    useEffect(() => {
        const socket = io("/api/admin/ws");
        socket.on("connect", () => {
            console.log(`Socket connection established!`);
          });
          socket.on("update-userData", (data) => {
            setUserData(data);
            console.log(userData);
          });
          socket.on("live-stats", (data) => {
            setLiveStats(data);
            console.log(data);
          });
    });
    return(
        <Row>
            <Card title="Live Streamers:" data="0" />
            <Card title="Online Players" data="182" />
            <Card title="Connected Servers" data="8" />
            <Card title="Live Streamers:" data="15" />
        </Row>
    );
}

const Row = styled.div`
    width: 100%;

    display: flex;
    justify-content: flex-start;
`;

export default Dashboard;
