import styled from 'styled-components'

import Card from '../stats-card'
import { useEffect, useState } from 'react';

/* Socket.io */
import { io } from "socket.io-client";
const socket = io("/api/admin/ws");


function Dashboard (props) {
    const [userData, setUserData] = useState();
    const [liveData, setLiveData] = useState({
        liveStreamCount: "...",
        liveServerCount: "...",
        livePlayerCount: "..."
    });
    useEffect(() => {
        socket.on("connect", () => {
            console.log(`Socket connection established!`);
          });
          socket.on("update-userData", (data) => {
            setUserData(data);
          });
          socket.on("stats", (data) => {
            setLiveData({
                ...liveData,
                ...data
            });
          });
    });
    return(
        <Row>
            <Card title="Live Streamers:" value={liveData.liveStreamCount} />
            <Card title="Online Players" value={liveData.livePlayerCount} />
            <Card title="Connected Servers" value={liveData.liveServerCount} />
            <Card title="Live Streamers:" value="15" />
        </Row>
    );
}

const Row = styled.div`
    width: 100%;

    display: flex;
    justify-content: flex-start;
`;

export default Dashboard;
