import styled from 'styled-components';


function Layout ({children}) {
    return(
        <>
            <PageWrapper>
                {children}
            </PageWrapper>
        </>
    );
}

const PageWrapper = styled.div`
    height: auto;
    overflow: hidden;
    background-color: #101824;
    background-size: auto;
`;



export default Layout;
