import styled from 'styled-components';
const userData = {};

function Layout ({children}) {
    
    return(
        <PageWrapper>
            <Header>
                <HeaderContentWrapper>
                    <HeaderText>UTHD MC</HeaderText>
                    <img id="profileImg" src=""></img>
                </HeaderContentWrapper>
            </Header>
            {children}
        </PageWrapper>
    );
    updateUserData();
}

async function updateUserData() {
    try{ //Retrieve data from REST API endpoint, on success, update the profile Pic and UserData object, on failure, log the error.
    const profileImgElement = document.getElementById("profileImg"); //Get element to display Name in
    const response = await axios.get('/api/users'); //waits for axios to complete GET request to /api/users, returns an object "user"
    profileImgElement.src = response.data.user.profileImg; //Pulls the name value from the "user" object and changes the welcome message to reflect
    userData = response.data.user;
    }
    catch(err) {
        console.log(err);
    }
}

const Header = styled.div`
    height: 80px;
    width: 100%;
    background-color: #000000;

    display: flex;
    justify-content: space-between;
    align-items: center;
    
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

const PageWrapper = styled.div`

`;

const HeaderText = styled.h1`

`;

const HeaderProfile = styled.img`
    width: 50px;
    background-color: gray;
    border-radius: 100%;
`;

const HeaderContentWrapper = styled.div`
    width: 90%;
    margin: 0 auto;
`;
export default Layout;
