import axios from 'axios'; //for API fetching
import styled from 'styled-components'; //for in-js styling

let userData = {}; //initializes page-wide object to allow acces to the data without another request to the API

/* React/NextJS Page Component -- Displayed after successful login */
export default function WelcomePage ({ data }) {
    return(
            <Container>
                <Nav><h3>Next.JS</h3><a href="/logout"><button>Log Out</button></a></Nav>
                <h1 id="welcomeText" onClick={showMessage}></h1>
                <p id="extraMessageContainer">This site is still under development and is primarily an excersize to learn a MENN Stack (MongoDB, ExpressJS, NextJS, NodeJS! Thanks for checking it out!</p>
            </Container>
    );
    updateUserName();
}

/* CLIENT SIDE JS */
async function updateUserName() {
    try{ //Retrieve data from REST API endpoint, on success, update the welcome message, on failure, log the error.
    const titleElement = document.getElementById("welcomeText"); //Get element to display Name in
    const response = await axios.get('/api/users'); //waits for axios to complete GET request to /api/users, returns an object "user"
    titleElement.innerHTML = `Hello, ${response.data.user.name}!`; //Pulls the name value from the "user" object and changes the welcome message to reflect
    userData = response.data.user;
    }
    catch(err) {
        console.log(err);
    }
}
function showMessage () { //Toggle whether the paragraph information is visible with CSS classes
    document.getElementById("extraMessageContainer").classList.toggle("hidden");
}

/* STYLED-COMPONENTS (CSS) */
const Container = styled.div`
    background-color:#36454F;
    height: 100%;
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: 900ms;
    h1 {
        margin: 0 0 0 0;
        font-family: 'Montserrat', sans-serif;
        font-weight: 900;
        font-size: 85px;
        color: white;
        padding: 50px;
        cursor: pointer;
    }
    p {
        margin: 0;
        font-family: 'Montserrat', sans-serif;
        font-weight: 400;
        font-size: 25px;
        color: white;
        padding: 50px;
    }
`;
const Nav = styled.div`
    height: 72px;
    width: 100%;
    background-color: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    h3 {
        font-family: 'Montserrat', sans-serif;
        font-weight: 700;
        font-size: 35px;
        color: white;
        margin-left: 25px;
        letter-spacing: 5px;
    }
    button {
        z-index: 10;
        font-family: 'Montserrat', sans-serif;
        font-weight: 500;
        font-size: 15px;
        color: black;
        background-color: white;
        outline: none;
        border: none;
        border-radius: 10px;
        padding: 5px 12px 5px 12px;
        margin-right: 15px;
        cursor: pointer;
    }
`;
const Page = styled.div`
width: 100%;
height: 100%;
background-color: #36454F;
`;
