//Component Imports
import ButtonHelp from "../../components/play-components/buttonHelp";
import ButtonHurt from "../../components/play-components/buttonHurt";
import Layout from "../../components/layout-components/layout";
import StreamerSidebar from "../../components/play-components/streamerSidebar";

//Useful Stuff Imports
import styled from "styled-components";
import dynamic from 'next/dynamic';
import { useEffect, useState } from "react";

/* Socket.io */
import { io } from "socket.io-client";
const socket = io("/api/ws/user");

const StreamPlayer = () => {
  if(typeof window !== 'undefined') {
    return <TwitchEmbed
      channel="uthd"
      id="uthd"
      muted
      theme="dark"
      height="100%"
      width="100%"
    />
  }else return  "Loading...";
}

function StreamerGameControl(props) {
  const [userData, setUserData] = useState();
  const [streamerData, setStreamerData] = useState();

  useEffect(() => {
    socket.on("connect", () => {});
    socket.on("update-userData", (data) => {
      setUserData(data);
    });
    socket.on("update-streamerData", (data) => {
      setStreamerData(data);
    });
    socket.emit("get-userData");
  }, []);
  return (
    <Layout>
      <StreamerSidebar streamerData={streamerData} />
      <ContentWrapper>
        <RowOneWrapper>
          <Twitch>
            <StreamPlayer  />
          </Twitch>
        </RowOneWrapper>
        <RowTwoWrapper>
          <GameControl>
            <ButtonHelp title="Enchant Held Item" action="enchant"></ButtonHelp>
            <ButtonHelp title="Heal Player" action="heal-player"></ButtonHelp>
            <ButtonHelp title="Give Item" action="give-item"></ButtonHelp>
            <ButtonHelp
              title="Give Good Potion Effect"
              action="good-potion"
            ></ButtonHelp>
            <ButtonHurt
              title="Give Bad Potion Effect"
              action="bad-potion"
            ></ButtonHurt>
            <ButtonHurt title="Clear Inventory" action="clear-inv"></ButtonHurt>
            <ButtonHurt title="Summon Mob" action="summon-mob"></ButtonHurt>
            <ButtonHurt title="etc."></ButtonHurt>
          </GameControl>
        </RowTwoWrapper>
        <RowThreeWrapper>
          <Stats>
            <h3>User Info</h3>
            <h5>
              <span>Shards: </span>
              {userData ? userData.stats.shards : `Loading...`}
            </h5>
          </Stats>
          <Leaderboard>
            <h3>Leaderboard</h3>
            <h5>
              <span>Top Player: </span>Loading...
            </h5>
          </Leaderboard>
        </RowThreeWrapper>
      </ContentWrapper>
    </Layout>
  );
}

const ContentWrapper = styled.div`
  min-height: calc(100vh - 80px);
  width: calc(100vw - 72px);
  margin-left: 72px;

  display: flex;
  flex-direction: column;
  align-items: center;
`;
const RowOneWrapper = styled.div`
  min-height: 800px;
  width: 80vw;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  overflow: hidden;
  margin: 0;
`;
const RowTwoWrapper = styled.div`
  justify-content: center;

  display: -webkit-box;
  display: -moz-box;
  display: -ms-flexbox;
  display: -moz-flex;
  display: -webkit-flex;
  display: flex;

  margin: 0;
`;
const RowThreeWrapper = styled.div`
  width: 100%;
  justify-content: center;

  display: -webkit-box;
  display: -moz-box;
  display: -ms-flexbox;
  display: -moz-flex;
  display: -webkit-flex;
  display: flex;
`;

const Twitch = styled.div`
  width: auto;
  min-width: 65vw;
  height: 720px;
  background-color: gray;
  margin: 20px;
  border-radius: 5px;
  border: 5px solid #f0524c;
`;
const GameControl = styled.div`
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  justify-self: left;
  margin: 0 50px;
`;
const Stats = styled.div`
  min-height: 500px;
  min-width: 300px;

  box-shadow: 0px 0px 0px 3px #f0524c;
  border-radius: 8px;
  background: none;

  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 25px;
  h3 {
    text-align: center;
    font-size: 36px;
    margin: 0 auto;
  }
  h5 {
    text-align: center;
    font-size: 20px;
    margin: 0 auto;
    font-weight: 200;
  }
  span {
    font-size: 24px;
    font-weight: 600;
  }
`;
const Leaderboard = styled.div`
  min-height: 500px;
  min-width: 300px;

  box-shadow: 0px 0px 0px 3px #f0524c;
  border-radius: 8px;
  background: none;

  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 25px;
  h3 {
    text-align: center;
    font-size: 36px;
    margin: 0 auto;
  }
  h5 {
    text-align: center;
    font-size: 20px;
    margin: 0 auto;
    font-weight: 200;
  }
  span {
    font-size: 24px;
    font-weight: 600;
  }
`;

//Props getting
/*
export async function getStaticPaths() {
    const paths = await getAllPageSlugs();
    return {
        fallback: 'blocking',
        paths
    }
  }

export async function getStaticProps({ params }) {
    const pageData = await getPageDataFromSlug(params.slug);
    return {
        revalidate: 5,
        props: {pageData}
    }
}

async function getAllPageSlugs () {
    const pages = await getPages();
    return pages.map(page => {
        return {
            params: {
                slug: page.slug
            }
        }
    })
}*/

export default StreamerGameControl;
