import styled from 'styled-components';

export default function Login (props) {
    return(
        <Container>
            <LoginWrapper>
                <Form autoComplete="off">
                    <h3>Login</h3>
                    <label htmlFor="username">E-Mail</label><br />
                    <input type="email" name="username"></input><br />
                    <label htmlFor="username">Password</label><br />
                    <input type="password" name="password"></input>
                </Form>
            </LoginWrapper>
        </Container>
    );
}

/* Styling */
const Container = styled.div`
    background-image: url(/login/login-bg.svg);
    background-size: contain
    background-color: red;
    z-index: -1;
`;

const LoginWrapper = styled.div`

`;

const Form = styled.form`
    float: left;

    display: flex;
    flex-direction: column;
    justify-content: left;
    text-align: center;

    width: 35%;
    min-width: 400px;
    height: 100vh;

    background: rgba(219, 234, 254, 0.69);
    box-shadow: 3px 0px 4px rgba(124, 124, 124, 0.45);


    @media only screen and (max-width: 650px) {
        width: 100%;
    }
    h3 {
        font-family: 'Montserrat', sans-serif;
        font-weight: 900;
        font-size: 65px;
    }
    input {
        width: 80%;
        margin: 5px 10% 20px 10%;
        padding: 10px;
        border: none;
        border-radius: 10px;
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.1);
    }
    input:focus {
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.35);
        transition: 300ms;
    }
    input:hover {
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        transition: 300ms;
    }

    label {
        text-align: left;
        margin: 0 0 5px 0;
        padding: 0 10%;

        font-family: 'Montserrat', sans-serif;
        font-weight: 500;
    }
`;
