import axios from 'axios';
let userData = {};

function Layout ({children}) {
    updateUserData();
    return(
        <>
            <PageWrapper>
                {children}
            </PageWrapper>
        </>
    );
}

const PageWrapper = styled.div`
    height: auto;
    overflow: hidden;
    background-color: #101824;
    background-size: auto;
`;
const SignInButton = styled.div`
    background-color: #9146FF;
