    }
`;
const SignInText = styled.h5`
    margin: 0;
    font-size: 15px;
`;

const HeaderMenu = styled.div`
    display: flex;
`;

const HeaderContentWrapper = styled.div`
    width: 90%;
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;



export default Layout;
