import styled from 'styled-components';
import Layout from '../components/layout';

function Home () {
    return(
        <Layout>
            <Canvas>
                <Wrapper>
                    <Title>Play along with your favorite streamers</Title>
                    <ButtonWrapper>
                 <SignUpButton>Sign Up</SignUpButton>
                    </ButtonWrapper>
                </Wrapper>
            </Canvas>

        </Layout>
    );
}

const Title = styled.h1`
    font-size: 96px;
    text-align: center;
    margin-bottom: 300px;
    color: #FF5953;
    text-shadow: 4px 6px 3px #101824;
`;

const Wrapper = styled.div`
    float: left;
    width: 75%;

    padding: 50px;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Canvas =styled.div`
    width: 100%;
    height: calc(100vh - 80px);
    background: url(/home/gaming-minecraft.png);
    background-size: cover;

    display: flex;
    align-items: center;
`;

const SignUpButton = styled.div`
    background-color: #9146FF;
    height: 44px;
    width: auto;
    padding: 0 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 5px;
    color: white;
`;

export default Home;
